import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import ConfirmationPopup from 'components/forms/ConfimationPopup';
import PublishProjectForm from 'components/forms/PublishProjectForm';
import ProjectPreview from './ProjectPreview';
import Box from '@mui/material/Box';
import SearchIcon from '@mui/icons-material/Search';


// Import Material-UI components
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableContainer, 
  TableHead, 
  TableRow, 
  TablePagination, 
  Paper, 
  Toolbar, 
  Typography, 
  IconButton, 
} from '@mui/material';
import { Delete, Search } from '@mui/icons-material';




function ActiveProjects() {

  const [projects, setProjects] = useState([]);
  const navigate = useNavigate();
  const [popup, setPopup] = useState(false);
  const [selectedProject, setSelectedProject] = useState("");
  const [preview ,setPreview] = useState(false);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  // api call to get the table
  React.useEffect(() => {
    async function fetchTableData() {
      try {
        const response = await axios.get('/api/projects', {
          params: {
            pending: false
          }
        });
        setProjects(response.data);
      } catch (error) {
        console.log(error);
      }
    }

    fetchTableData();
    }, []);

  const handleClick= (id) => {
      setPopup(true);
      setSelectedProject(id);
  }

  const handleClose = () => {
    setPreview(false);
  }

  const handleYes = async() => {
    try {
      console.log(selectedProject)
      const response = await axios.delete('/api/project/'+ selectedProject);

      switch (response.status) {
        case (200):
          console.log("Deleted successfully");
          setPopup(false);
          setProjects(projects.filter(p => p._id !== selectedProject));
          setSelectedProject("");
          break;
        case (401):
          console.log("Unauthorized to delete");
          navigate("/signin" , {replace: true});
          break;

        case(404):
          console.log("Cannot find the project");
          break;

        case(500):
          console.log("server error");
          break;
      }

    } catch (error) {
      console.log(error);
    }
  }

  const handleNo = (event) => {
    setPopup(false);
  }


  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };


  return (
    <>
      <PublishProjectForm />
      {popup && <ConfirmationPopup message="Are you sure you want to delete this item ?"  onNo={handleNo} onYes={handleYes}/>}
      {preview && <ProjectPreview projectId={selectedProject}  handleClose={handleClose} />}

      <Paper>
        <Toolbar>

         <Typography
           variant="h6"
           noWrap
           component="div"
           sx={{ flexGrow: 1, display: { xs: 'none', sm: 'block' } }}
         >
           ACTIVE PROJECTS
         </Typography>
         <Search>
           <SearchIconWrapper>
             <SearchIcon />
           </SearchIconWrapper>
           <StyledInputBase
             placeholder="Searchâ€¦"
             inputProps={{ 'aria-label': 'search' }}
           />
         </Search>
         <Box sx={{ml:3}} ></Box>
         <PublishProjectForm />
        </Toolbar>
        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>S.No</TableCell>
                <TableCell>Project Name</TableCell>
                <TableCell>Skills</TableCell>
                <TableCell>Created On</TableCell>
                <TableCell>Created By</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {projects
                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                .map((row, index) => {
                  return (
                    <TableRow key={row._id}>
                      <TableCell>{index + 1 + page * rowsPerPage}</TableCell>
                      <TableCell onClick={() => {setSelectedProject(row._id); setPreview(true);}}>{row.name}</TableCell>
                      <TableCell>{row.skills}</TableCell>
                      <TableCell>{row.createdAt}</TableCell>
                      <TableCell>{row.createdBy}</TableCell>
                      <TableCell>
                        <IconButton onClick={() => handleClick(row._id)} color="error">
                          <Delete />
                        </IconButton>
                      </TableCell>
                    </TableRow>
                  );
                })}
            </TableBody>
          </Table>
        </TableContainer>
        <TablePagination
          component="div"
          count={projects.length}
          page={page}
          onPageChange={handleChangePage}
          rowsPerPage={rowsPerPage}
          onRowsPerPageChange={handleChangeRowsPerPage}
        />
      </Paper>
    </>
  );
}

export default ActiveProjects;
